<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Courses extends CI_Controller {    public function __construct() {        parent::__construct();        if (!$this->tank_auth->is_logged_in()) {         // logged in            redirect('auth/');        }    }    public function index() {        //$this->load->view('welcome');    }    public function create_course() {        $data['main_menu'] = 'dashboard/menu/main_ menu_teacher1';        $data['user_profile'] = 'dashboard/notification/user_profile_teacher';        $data['main_content'] = 'dashboard/content/teacher/create_course';        $data['content_right'] = '';        $this->load->view('dashboardTemplate1', $data);    }    function convert_to_gmt($time = '', $timezone = 'UTC', $dst = FALSE) {        if ($time == '') {            return now();        }        $time -= timezones($timezone) * 3600;        if ($dst == TRUE) {            $time -= 3600;        }        return $time;    }}