cho date('m/d/Y H:i:s', gmt_to_local(strtotime($trans->Date), $TimeZone, $DayLightSaving)); ?> </td>                            <td class="center"><?php echo $trans->Description; ?></td>                            <?php                            //to get Teacher Name                            $SQLComm = "SELECT username from users WHERE email = '" . $trans->TeacherID . "' ";                            $teacher_name = $this->db->query($SQLComm);                            foreach ($teacher_name->result() as $teacher) {                                ?><td class="center"><?php echo $teacher->username; ?></td><?php                            }                            ?>                            <td class="center">                                <?php                                if ($trans->Status == "Success"):                                    echo "<span class='label label-success'>Success</span>";                                elseif ($trans->Status == "Pending"):                                    echo "<span class='label label-warning'>Pending</span>";                                elseif ($trans->Status == "Processing"):                                    echo "<span class='label label-warning'>Processing</span>";                                elseif ($trans->Status == "Blocked"):                                    echo "<span class='label label-important'>Blocked</span>";                                endif;                                ?>                            </td>                            <td class="center">                                <?php echo $trans->Amount; ?>	                            </td>                        </tr>                    <?php } ?>                </tbody>            </table>                    </div>    </div></div><!--/span-->