<script type="text/javascript" src="//cdnjs.cloudflare.com/ajax/libs/jstimezonedetect/1.0.4/jstz.min.js"></script><?php $this->load->helper('share'); ?><?php $TimeZone = "UTC"; ?><script type="text/javascript">    var TimeZoneResult = '';    $(document).ready(function() {        var visitortime = new Date();        var timediff = -visitortime.getTimezoneOffset();        var visitortimezone = timediff / 60;        var modresult;        var result = '';        if (visitortimezone == 0) {            TimeZoneResult = "UTC";        }        else if (visitortimezone > 0) {            if (timediff % 60 == 0)                result += Math.floor(parseInt(visitortimezone)).toString();            else if (timediff % 30 == 0)                result += Math.floor(parseInt(visitortimezone)).toString() + '5';            else                result += Math.floor(parseInt(visitortimezone)).toString() + '75';            TimeZoneResult = "UP" + result;        }        else if (visitortimezone < 0) {            visitortimezone *= -1;            if (timediff % 60 == 0)                result += Math.floor(parseInt(visitortimezone)).toString();            else if (timediff % 30 == 0)                result += Math.floor(parseInt(visitortimezone)).toString() + '5';            else                result += Math.floor(parseInt(visitortimezone)).toString() + '75';            TimeZoneResult = "UM" + result;        }    });</script><?php$DayLightSaving = 0;$email = $this->tank_auth->get_user_email();$TimeZoneData = $this->db->query('SELECT TimeZone, DayLightSaving FROM tbl_profile where UserID ="' . $email . '"');if ($TimeZoneData->num_rows() != 0) {    foreach ($TimeZoneData->result() as $TimeZ) {        $TimeZone = $TimeZ->TimeZone;        $DayLightSaving = $TimeZ->DayLightSaving;    }} else {    $TimeZone = $this->session->userdata('TimeZone');    //echo $TimeZone;}//End TimeZone ?><div class="col-lg-9">    <?php//$this->lang->load('tank_aut