<?php$this->lang->load('tank_auth');$email = $this->tank_auth->get_user_email();$msg_count = $this->mahana_messaging->get_all_threads($email);$SQLCom = "Select * from msg_status where user_id = '" . $email . "' and status = '0'";$messagecount = $this->db->query($SQLCom);?><li class="dropdown hidden-xs">    <a class="btn dropdown-toggle" data-toggle="dropdown" href="index.html#">        <?php if ($messagecount->num_rows() != 0) { ?>            <span class="notification red" style = "font-size: 10px !important; margin-top: 10px !important; padding:2px; margin-right:15px;">                <?php echo $messagecount->num_rows(); ?>                          </span>        <?php } ?>        <i class="fa fa-envelope"></i>    </a>    <ul class="dropdown-menu messages">        <li class="dropdown-menu-title">            <span>You have <?php echo $messagecount->num_rows(); ?>  messages</span>        </li>        <?php        $MessageSmall = $this->mahana_messaging->get_all_threads($email);        $i = 0;        if ($MessageSmall['err'] != 1) {            foreach ($MessageSmall['retval'] as $MessageB) {                $SQLPicture = "Select Picture from tbl_profile where UserID = '" . $MessageB['sender_id'] . "'";                $PicData = $this->db->query($SQLPicture);                foreach ($PicData->result() as $Pic) {                    $Pict = $Pic->Picture;                }                if ($MessageB['status'] == 0) {                    ++$i;                    if ($i == 11)                        break;                    ?>                    <li>                        <a href="<?php echo base_url() . 'messaging/read_single_message_all_threads/' . $MessageB['id'] . '/' . base64_encode($MessageB['sender_id']); ?>">                            <span class="avatar"><img src="<?php echo base_url(); ?>public/dashboard/upl/<?php echo $Pict; ?>" alt="Picture" /></span>                            <span class="header">                                <span class="from">                                    <?php echo $MessageB['username']; ?>                                </span>                            </span>                            <span class="message">                                <?php echo substr($MessageB['subject'], 0, 25) . " ..."; ?>                            </span>                          </a>                    </li>                    <?php                }            }            ?>            <li class="dropdown-menu-sub-footer">                <a href="<?php echo site_url('messaging'); ?>" class="dropdown-menu-sub-footer">View all messages</a>            </li>	        <?php } ?>    </ul></li>