<div class="row" style="margin-top: 200px;">    <div class="col-lg-12">        <?php        $attributes = array('class' => 'form-horizontal', 'role' => 'form', 'id' => 'forget_password');        echo form_open_multipart('auth/forgot_password', $attributes);        ?>        <fieldset>	            <div class="form-group">                <label for="login" class="col-sm-2 col-lg-4 control-label" style="color: white;" >Email Address</label>                <div class="col-sm-10 col-lg-8">                    <input type="text" name="login" id="login" class="form-control" />								                </div>            </div>            <div class="form-group">                <div class="col-sm-2 col-lg-4"></div>                <div class="col-sm-10 col-lg-12 ">                    <div class="center-block text-center" >                        <input type="submit" name="next" value="Submit" class="btn btn-success" />                    </div>                </div>            </div>        </fieldset>        <?php echo form_close(); ?>    </div></div><script type="text/javascript">    $(document).ready(function() {        $("#forget_password").validate({            rules: {                login: {                    required: true,                    email: true                }            },            messages: {                login: "Please enter a valid email address"            },            highlight: function(element) {                $(element).closest('.control-group').removeClass('success').addClass('error');            },            success: function(element) {                element                        .text('OK!').addClass('valid')                        .closest('.control-group').removeClass('error').addClass('success');            }        });    });</script>